import{_ as t,c as r,o,a4 as e}from"./chunks/framework.4aTu-Nia.js";const u=JSON.parse('{"title":"北医三院（北京大学附属第三医院）","description":"","frontmatter":{},"headers":[],"relativePath":"resource/beijing.md","filePath":"resource/beijing.md"}'),n={name:"resource/beijing.md"},s=e('<h1 id="北医三院-北京大学附属第三医院" tabindex="-1">北医三院（北京大学附属第三医院） <a class="header-anchor" href="#北医三院-北京大学附属第三医院" aria-label="Permalink to &quot;北医三院（北京大学附属第三医院）&quot;">​</a></h1><blockquote><h2 id="概况" tabindex="-1"><strong>概况</strong> <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;**概况**&quot;">​</a></h2></blockquote><p><strong><a href="https://www.puh3.net.cn/" target="_blank" rel="noreferrer">北京大学第三医院</a>（Peking University Third Hospital，PUTH）是集医疗、教学、科研、预防保健、康复与健康管理为一体的综合性三甲医院。北京大学第三医院部分医生一直致力于跨性别医疗事业的发展，创建了一支跨性别序列医疗团队，并设置了跨性别专科门诊。具体而言，如果符合要求，可以在北京大学第三医院获得易性症诊断及进行 HRT，这包括了定期检查、获得用药建议及购买部分药物。北京大学第三医院也可进行性别重置手术。</strong></p><p><strong>无需身份证或就诊卡即可就诊</strong></p><p>北医三院跨性别序列医疗团队微信公众号：<a href="https://open.weixin.qq.com/qr/code?username=a14chuang" target="_blank" rel="noreferrer">| 本来模样 |</a></p><blockquote><h2 id="地址" tabindex="-1"><strong>地址</strong> <a class="header-anchor" href="#地址" aria-label="Permalink to &quot;**地址**&quot;">​</a></h2></blockquote><p><strong>北京大学第三医院设有多个院区。</strong></p><ul><li><strong>北京大学第三医院（主院区）：位于<a href="https://amap.com/place/B000A7CGSS" target="_blank" rel="noreferrer">北京市海淀区花园北路 49 号</a>，刘烨、潘柏林等医生通常在此出诊。</strong></li><li><strong>生殖医学中心：位于<a href="https://amap.com/place/B000A85I42" target="_blank" rel="noreferrer">北京市海淀区学院路甲 38 号</a>，主院区旁边。如果要检测染色体需要在这里领取结果。</strong></li></ul><blockquote><h2 id="预约挂号" tabindex="-1"><strong>预约挂号</strong> <a class="header-anchor" href="#预约挂号" aria-label="Permalink to &quot;**预约挂号**&quot;">​</a></h2></blockquote><table><thead><tr><th><strong>出诊医生</strong></th><th><strong>出诊时间</strong></th><th><strong>门诊名称</strong></th><th><strong>门诊地点</strong></th></tr></thead><tbody><tr><td><strong>潘柏林</strong></td><td><strong>周二晚间(17:30-20:00)</strong></td><td><strong>易性症综合治疗门诊(特需)</strong></td><td><strong>健康医学楼七层</strong></td></tr><tr><td><strong>常旭</strong></td><td><strong>周一上午</strong></td><td><strong>易性症综合治疗门诊</strong></td><td><strong>医疗美容楼一层10诊室</strong></td></tr><tr><td><strong>赵润蕾</strong></td><td><strong>周二上午</strong></td><td><strong>易性症综合治疗门诊</strong></td><td><strong>医疗美容中心一层1诊室</strong></td></tr><tr><td><strong>刘烨</strong></td><td><strong>周四下午</strong></td><td><strong>易性症激素治疗门诊</strong></td><td><strong>门诊楼三层内分泌专家诊区</strong></td></tr></tbody></table><blockquote><p><strong>注：姐妹们可以放心挂号，进去后放心说，医生们都非常友跨的！！！</strong></p></blockquote><p>转自mtf.wiki（有修改）</p>',12),a=[s];function g(d,l,i,h,c,p){return o(),r("div",null,a)}const b=t(n,[["render",g]]);export{u as __pageData,b as default};
