import{_ as e,c as t,o as r,a4 as a}from"./chunks/framework.4aTu-Nia.js";const u=JSON.parse('{"title":"复旦儿科","description":"","frontmatter":{},"headers":[],"relativePath":"resource/shanghai/fdek.md","filePath":"resource/shanghai/fdek.md"}'),o={name:"resource/shanghai/fdek.md"},n=a('<h1 id="复旦儿科" tabindex="-1">复旦儿科 <a class="header-anchor" href="#复旦儿科" aria-label="Permalink to &quot;复旦儿科&quot;">​</a></h1><blockquote><p><strong>复旦儿科的跨性别 MDT 门诊是由心理科、内分泌遗传代谢科、泌尿外科、社工部共同参与的，旨在帮助广大跨性别儿童青少年的多学科门诊。通过多学科医生的专业评估、诊断以及必要药物干预，对于减少这些特殊儿童青少年人群的负面情绪、提高学习成绩以及改善家庭关系、构建和谐社会方面有很大的帮助。 <a href="https://ch.shmu.edu.cn/news/content/pid/13742.html" target="_blank" rel="noreferrer">ch.shmu.edu.cn/news/content/pid/13742.html</a></strong></p></blockquote><p><strong><a href="https://amap.com/place/B00155KP0U" target="_blank" rel="noreferrer">复旦大学附属儿童医院</a></strong></p><p><strong>门诊时间：每周四下午</strong></p><p><strong>预约电话：<a href="tel:+8602164932953" target="_blank" rel="noreferrer">+86 021-64932953</a></strong></p><blockquote><p><strong>目前，未成年跨性别者在前往一些医院时，可能会被推荐/转诊至上海复旦儿科跨性别 MDT 医疗联系 罗飞宏 医生。</strong></p></blockquote><p>转自mtf.wiki</p>',7),s=[n];function c(_,p,h,l,d,i){return r(),t("div",null,s)}const g=e(o,[["render",c]]);export{u as __pageData,g as default};
